SELECT idAutos AS 'Identificador', fechaCompra AS 'Fecha Compra', precioCompra AS 'Precio Compra', nombreLugarCompra AS 'Lugar de Compra', nombreModelo AS 'Modelo', nombreArmadoras AS 'Armadora', nombreJugueteras AS 'Juguetera', nombreColor AS 'Color Principal' FROM autos JOIN lugarescompra on autos.idLugarCompra=lugarescompra.idlugaresCompra JOIN modelos ON autos.idModeloAuto=modelos.idModelos 
JOIN coloresporauto ON autos.idAutos=coloresporauto.idAuto JOIN colores ON coloresporauto.colorCarroceriaPrincipal=colores.idColores JOIN Armadoras ON modelos.idMarcaArmadora=Armadoras.idArmadoras JOIN Jugueteras ON Modelos.idMarcaJuguetera=Jugueteras.idJugueteras